mapper.select:
SELECT c.COMMENT_ID, c.BOARD_ID, c.BOARD_TYPE, c.PARENT_COMMENT_ID, c.USER_ID, c.CONTENT, c.LIKE_COUNT, c.IS_DELETED, c.CREATED_AT, c.UPDATED_AT, u.USER_NICKNAME FROM `COMMENT` c LEFT JOIN USERS u ON c.USER_ID = u.USER_ID WHERE c.PARENT_COMMENT_ID IN
#{parentId}
AND c.IS_DELETED = FALSE ORDER BY c.CREATED_AT ASC

mapper.update:
UPDATE `COMMENT`
SET LIKE_COUNT = LIKE_COUNT + 1
WHERE COMMENT_ID = #{commentId}

mapper.select:
SELECT EXISTS(
SELECT 1
FROM `COMMENT`
WHERE COMMENT_ID = #{commentId}
AND USER_ID = #{userId}
)
