mapper.select:
SELECT f.FREEBOARD_ID, f.USER_ID, u.USER_NICKNAME, f.FREEBOARD_TITLE, SUBSTRING(f.FREEBOARD_PLAIN_TEXT, 1, 200) as FREEBOARD_SUMMARY, f.FREEBOARD_CLICK, f.FREEBOARD_REPRESENT_IMAGE, f.FREEBOARD_CREATED_AT, (SELECT COUNT(*) FROM `LIKE` l WHERE l.REFERENCE_ID = f.FREEBOARD_ID AND l.REFERENCE_TYPE = 'POST_FREEBOARD') AS LIKE_COUNT, (SELECT COUNT(*) FROM `COMMENT` c WHERE c.BOARD_ID = f.FREEBOARD_ID AND c.BOARD_TYPE = 'FREEBOARD' AND c.IS_DELETED = FALSE) AS COMMENT_COUNT FROM FREEBOARD f LEFT JOIN USERS u ON f.USER_ID = u.USER_ID
f.FREEBOARD_DELETED_YN = 'N'
AND ( f.FREEBOARD_TITLE LIKE CONCAT('%', #{search.keyword}, '%') OR f.FREEBOARD_PLAIN_TEXT LIKE CONCAT('%', #{search.keyword}, '%') OR u.USER_NICKNAME LIKE CONCAT('%', #{search.keyword}, '%') OR EXISTS ( SELECT 1 FROM FREEBOARD_TAG ft WHERE ft.FREEBOARD_ID = f.FREEBOARD_ID AND ft.TAG_DISPLAY_NAME LIKE CONCAT('%', #{search.keyword}, '%') ) )
ORDER BY
LIKE_COUNT ${sort.directionSql}
COMMENT_COUNT ${sort.directionSql}
f.FREEBOARD_CLICK ${sort.directionSql}
f.FREEBOARD_CREATED_AT ${sort.directionSql}
LIMIT #{page.size} OFFSET #{page.offset}

mapper.update:
UPDATE FREEBOARD
SET FREEBOARD_CLICK = FREEBOARD_CLICK + 1
WHERE FREEBOARD_ID = #{freeboardId}
