mapper.update:
UPDATE payments
SET status = #{toStatus}
WHERE order_id = #{orderId}
  AND status = #{fromStatus}

mapper.select:
SELECT p.order_id, p.user_id, p.plan_code, p.payment_key, p.order_name, p.customer_name, p.original_amount, p.used_point, p.amount, p.status, p.requested_at, p.canceled_at, p.pay_method, p.pg_raw_response, p.card_company, p.card_approval_no, p.approved_at, s.status AS subscription_status, s.start_date AS subscription_start_at, s.end_date AS subscription_end_at FROM payments p LEFT JOIN subscriptions s ON s.order_id = p.order_id
p.user_id = #{userId}
AND p.requested_at >= #{from}
AND p.requested_at < #{to}
AND p.status = #{status}
ORDER BY p.requested_at DESC
