mapper.insert:
INSERT INTO USERS (
    USER_EMAIL, USER_PW, USER_NAME, USER_NICKNAME, USER_IMAGE
) VALUES (
             #{email}, #{password}, #{name}, #{nickname}, #{image}
         )

mapper.select:
SELECT u.*
FROM USERS u
         JOIN SOCIALLOGIN s ON u.USER_ID = s.USER_ID
WHERE s.SOCIALLOGIN_PROVIDER = #{provider}
  AND s.SOCIALLOGIN_PROVIDER_ID = #{providerId}

mapper.insert:
INSERT INTO SOCIALLOGIN (
    USER_ID, SOCIALLOGIN_PROVIDER,
    SOCIALLOGIN_PROVIDER_ID, SOCIALLOGIN_SOCIAL_EMAIL
) VALUES (
             #{userId}, #{provider}, #{providerId}, #{email}
         )

mapper.update:
UPDATE USERS
SET USER_ISDELETED = 1
WHERE USER_ID = #{id}
