mapper.select:
SELECT u.*
FROM USERS u
         JOIN SOCIALLOGIN s ON u.USER_ID = s.USER_ID
WHERE s.SOCIALLOGIN_PROVIDER = #{provider}
  AND s.SOCIALLOGIN_PROVIDER_ID = #{providerId}

mapper.insert:
INSERT INTO SOCIALLOGIN (
    USER_ID, SOCIALLOGIN_PROVIDER,
    SOCIALLOGIN_PROVIDER_ID, SOCIALLOGIN_SOCIAL_EMAIL
) VALUES (
             #{userId}, #{provider}, #{providerId}, #{userEmail}
         )

mapper.delete:
DELETE FROM SOCIALLOGIN
WHERE USER_ID = #{userId}
  AND SOCIALLOGIN_PROVIDER = #{provider}

mapper.select:
SELECT COUNT(*)
FROM SOCIALLOGIN
WHERE USER_ID = #{userId}
  AND SOCIALLOGIN_PROVIDER = #{provider}

mapper.update:
UPDATE USERS
SET USER_ISDELETED = 1
WHERE USER_ID = #{userId}
